import React from "react";
import {
  Box,
  Heading,
  Paragraph,
  Text,
  Avatar,
  ResponsiveContext
} from "grommet";

import Resume from "./Resume";
import Description from "../desc";

const HomePage = () => {
  return (
    <ResponsiveContext.Consumer>
      {(size) => (
        <Box
          flex
          direction="row"
          align="start"
          justify="around"
          background="home"
          overflow={{ horizontal: "hidden" }}
        >
          {size === "large" ? (
            <Box>
              <Avatar
                border={{
                  color: "brand",
                  size: "small",
                  style: "solid"
                }}
                round="full"
                src="/website-images/site-512.png"
                size="200px"
                alignSelf="start"
              />
              <Heading textAlign="center">hello...</Heading>
              <Paragraph size="large" textAlign="end">
                {Description.ABOUT_ME}
              </Paragraph>
              <Text size="medium" textAlign="end">
                thanks for the visit!
              </Text>
              <Heading textAlign="center">...in there</Heading>
              <Avatar
                border={{
                  color: "brand",
                  size: "small",
                  style: "solid"
                }}
                round="full"
                src="/website-images/site-512.png"
                size="200px"
                alignSelf="end"
              />
            </Box>
          ) : (
            <Box align="center">
              <Avatar
                margin={{ top: "medium" }}
                border={{
                  color: "brand",
                  size: "small",
                  style: "solid"
                }}
                round="full"
                src="/website-images/site-512.png"
                size={size === "medium" ? "200px" : "125px"}
                alignSelf="center"
              />
              <Heading textAlign="center">welcome!</Heading>
              <Paragraph
                size="large"
                textAlign="center"
                margin={{ bottom: "large" }}
              >
                {Description.ABOUT_ME}
              </Paragraph>
              <Resume />
            </Box>
          )}
          {size === "large" ? (
            <Box>
              <Resume />
            </Box>
          ) : null}
        </Box>
      )}
    </ResponsiveContext.Consumer>
  );
};

export default HomePage;
