import React from "react";
import { Box } from "grommet";
import {
  Reactjs,
  Play,
  Language,
  System,
  Swift,
  Android,
  Car,
  Yoga,
  Multimedia
} from "grommet-icons";

import {
  VerticalTimeline,
  VerticalTimelineElement
} from "react-vertical-timeline-component";
import "react-vertical-timeline-component/style.min.css";

const Code = (props: any) => {
  return (
    <Box
      flex
      direction="column"
      align="center"
      justify="start"
      background="home"
    >
      <VerticalTimeline>
        <VerticalTimelineElement
          position="right"
          contentStyle={
            props.mode === "dark"
              ? { background: "#A2423D" }
              : { background: "rgb(255, 255, 255)" }
          }
          contentArrowStyle={
            props.mode === "dark"
              ? { borderRight: "7px solid  #A2423D" }
              : { borderRight: "7px solid  rgb(255, 255, 255)" }
          }
          className="vertical-timeline-element--work"
          date="apr 2020 - present"
          iconStyle={
            props.mode === "dark"
              ? { background: "#7D4CDB" }
              : { background: "#6FFFB0" }
          }
          icon={<Yoga color={props.mode === "dark" ? "accent-1" : "brand"} />}
        >
          <h3 className="vertical-timeline-element-title">collections v2</h3>
          <h4 className="vertical-timeline-element-subtitle">
            react web/native application
          </h4>
          <p>
            a reimagining of my passion project in both a web and mobile form
            that was previously attempted as a senior-year college project
          </p>
          <p style={{ color: "6f6f6f" }}>
            react web | react native | typescript | javascript | google firebase
            | tMDB api | grommet
          </p>
        </VerticalTimelineElement>
        <VerticalTimelineElement
          position="left"
          contentStyle={
            props.mode === "dark"
              ? { background: "#00739D" }
              : { background: "rgb(255, 255, 255)" }
          }
          contentArrowStyle={
            props.mode === "dark"
              ? { borderRight: "7px solid  #00739D" }
              : { borderRight: "7px solid  rgb(255, 255, 255)" }
          }
          className="vertical-timeline-element--work"
          date="apr 2020 - present"
          iconStyle={
            props.mode === "dark"
              ? { background: "#7D4CDB" }
              : { background: "#6FFFB0" }
          }
          icon={<Yoga color={props.mode === "dark" ? "accent-1" : "brand"} />}
        >
          <h3 className="vertical-timeline-element-title">personal website</h3>
          <h4 className="vertical-timeline-element-subtitle">
            react web application
          </h4>
          <p>
            my online portfolio; developed with typescript, grommet ui, and
            node.js
          </p>
        </VerticalTimelineElement>
        <VerticalTimelineElement
          position="right"
          contentStyle={
            props.mode === "dark"
              ? { background: "#A2423D" }
              : { background: "rgb(255, 255, 255)" }
          }
          contentArrowStyle={
            props.mode === "dark"
              ? { borderRight: "7px solid  #A2423D" }
              : { borderRight: "7px solid  rgb(255, 255, 255)" }
          }
          className="vertical-timeline-element--work"
          date="jan 2020 - present"
          iconStyle={
            props.mode === "dark"
              ? { background: "#7D4CDB" }
              : { background: "#6FFFB0" }
          }
          icon={<Car color={props.mode === "dark" ? "accent-1" : "brand"} />}
        >
          <h3 className="vertical-timeline-element-title">
            parkour - a residential parking sharing system
          </h3>
          <h4 className="vertical-timeline-element-subtitle">
            react native mobile application
          </h4>
          <p>
            senior design project that allows users to find parking spaces or
            host parking spaces; developed using the mern stack, typescript, and
            redux
          </p>
        </VerticalTimelineElement>
        <VerticalTimelineElement
          position="left"
          contentStyle={
            props.mode === "dark"
              ? { background: "#00739D" }
              : { background: "rgb(255, 255, 255)" }
          }
          contentArrowStyle={
            props.mode === "dark"
              ? { borderRight: "7px solid  #00739D" }
              : { borderRight: "7px solid  rgb(255, 255, 255)" }
          }
          className="vertical-timeline-element--work"
          date="nov 2019"
          iconStyle={
            props.mode === "dark"
              ? { background: "#7D4CDB" }
              : { background: "#6FFFB0" }
          }
